SELECT id,BLOCK_PARAMETERS_PARAMETER_optional,BLOCK_PARAMETERS_PARAMETER_KEY,BLOCK_PARAMETERS_PARAMETER_DESC,BLOCK_PARAMETERS_PARAMETER_DATATYPE,BLOCK_PARAMETERS_PARAMETER_VALUE,BLOCK_PARAMETERS_PARAMETER_DEFAULT FROM ib1_procedures_procedure_block_parameters_parameter
platform_name = #{platform_name}
and IB1_VERSION = #{IB1_VERSION}
and diagnose_adr = #{diagnose_adr}
and BLOCK_NAME = #{BLOCK_NAME}

SELECT ipp.BLOCK_KD_relevant BLOCK_KD_relevant, ipp.BLOCK_P_relevant BLOCK_P_relevant, ipp.BLOCK_optional BLOCK_optional, ipp.BLOCK_shortname BLOCK_shortname, ipp.BLOCK_standardIBNB BLOCK_standardIBNB, ipp.BLOCK_version BLOCK_version, ipp.BLOCK_NAME BLOCK_NAME, ipp.BLOCK_NAME_EN BLOCK_NAME_EN, ipp.BLOCK_DESC BLOCK_DESC,
ipp.BLOCK_TAGS_TAG BLOCK_TAGS_TAG, ipp.BLOCK_AREA BLOCK_AREA, ipp.BLOCK_LOGICAL_LINK BLOCK_LOGICAL_LINK, ipp.BLOCK_LAS BLOCK_LAS, ipp.BLOCK_CONDITIONS_DEPENDENCIES BLOCK_CONDITIONS_DEPENDENCIES, ipp.BLOCK_CONDITIONS_BUILDUP_CONDITIONS BLOCK_CONDITIONS_BUILDUP_CONDITIONS, ipp.BLOCK_CONDITIONS_GENERAL_CONDITIONS BLOCK_CONDITIONS_GENERAL_CONDITIONS, ipp.BLOCK_CONDITIONS_RESSOURCES BLOCK_CONDITIONS_RESSOURCES, ipp.BLOCK_CONDITIONS_MISCELLANEOUS BLOCK_CONDITIONS_MISCELLANEOUS, ipp.BLOCK_CONDITIONS_MANUAL_OPERATIONS BLOCK_CONDITIONS_MANUAL_OPERATIONS, ipp.BLOCK_POLLINTERVALL BLOCK_POLLINTERVALL, ipp.BLOCK_TIMEOUT BLOCK_TIMEOUT, ipp.BLOCK_IBNZ_VSI BLOCK_IBNZ_VSI, ipp.BLOCK_IBNZ_VSI_VERIFIED BLOCK_IBNZ_VSI_VERIFIED,
ippbpp.BLOCK_PARAMETERS_PARAMETER_optional BLOCK_PARAMETERS_PARAMETER_optional, ippbpp.BLOCK_PARAMETERS_PARAMETER_KEY BLOCK_PARAMETERS_PARAMETER_KEY, ippbpp.BLOCK_PARAMETERS_PARAMETER_DESC BLOCK_PARAMETERS_PARAMETER_DESC, ippbpp.BLOCK_PARAMETERS_PARAMETER_DATATYPE BLOCK_PARAMETERS_PARAMETER_DATATYPE, ippbpp.BLOCK_PARAMETERS_PARAMETER_VALUE BLOCK_PARAMETERS_PARAMETER_VALUE, ippbpp.BLOCK_PARAMETERS_PARAMETER_DEFAULT BLOCK_PARAMETERS_PARAMETER_DEFAULT
FROM IB1_PROCEDURES_PROCEDURE_BLOCK_PARAMETERS_PARAMETER AS ippbpp INNER JOIN IB1_PROCEDURES_PROCEDURE AS ipp
ON ippbpp.platform_name = ipp.platform_name AND ippbpp.diagnose_adr = ipp.diagnose_adr AND ippbpp.IB1_VERSION = ipp.IB1_VERSION AND ippbpp.BLOCK_NAME = ipp.BLOCK_NAME
ippbpp.platform_name = #{platform_name}
and ippbpp.IB1_VERSION = #{IB1_VERSION}
and ippbpp.diagnose_adr = #{diagnose_adr}
and ippbpp.BLOCK_NAME = #{BLOCK_NAME}
