select * from ib1_procedures_procedure_block_steps_step


     platform_name = #{platform_name}


     and IB1_VERSION = #{IB1_VERSION}


     and diagnose_adr = #{diagnose_adr}


     and BLOCK_NAME = #{BLOCK_NAME}


     and STEP_NAME = #{STEP_NAME}


     and BLOCK_NAME = #{BLOCK_NAME}


     and BLOCK_NAME = #{BLOCK_NAME}

SELECT BLOCK_id, STEP_id, STEP_NAME FROM IB1_PROCEDURES_PROCEDURE_BLOCK_STEPS_STEP


        platform_name = #{platform_name}


        and IB1_VERSION = #{IB1_VERSION}


        and diagnose_adr = #{diagnose_adr}


        and BLOCK_NAME = #{BLOCK_NAME}


        and BLOCK_id = #{BLOCK_id}


        and STEP_id = #{STEP_id}
