mapper.select:
select id,IB1_NAME,BLOCK_NAME,BLOCK_id,STEP_NAME,STEP_id,DIAG_PARAMETER_LONG_NAME,DIAG_PARAMETER_SHORT_NAME,DIAG_PARAMETER_MESSAGE_TYPE,DIAG_PARAMETER_RESPONSE_SHORT_NAME,DIAG_PARAMETER_PATH,DIAG_PARAMETER_DATATYPE,DIAG_PARAMETER_VALUE_PROXY_VALUE,DIAG_PARAMETER_VALUE_PROXY_TOLERANCE_MIN_VALUE,DIAG_PARAMETER_VALUE_PROXY_TOLERANCE_MAX_VALUE,DIAG_PARAMETER_DISPLAYUNIT from ib1_procedures_procedure_block_steps_step_diag_parameters


    platform_name = #{platform_name}


    and IB1_VERSION = #{IB1_VERSION}


    and diagnose_adr = #{diagnose_adr}


    and BLOCK_NAME = #{BLOCK_NAME}


    and STEP_NAME = #{STEP_NAME}


    and BLOCK_id = #{BLOCK_id}


    and STEP_id = #{STEP_id}

select DISTINCT STEP_DIAG_PARAMETERS_DATA_IDENTIFIER from ib1_procedures_procedure_block_steps_step_diag_parameters


        platform_name = #{platform_name}


        and IB1_VERSION = #{IB1_VERSION}


        and diagnose_adr = #{diagnose_adr}


        and BLOCK_NAME = #{BLOCK_NAME}


        and STEP_NAME = #{STEP_NAME}


        and BLOCK_id = #{BLOCK_id}


        and STEP_id = #{STEP_id}

select * from( SELECT ib.platform_name platform_name, ib.diagnose_adr diagnose_adr, ib.IB1_VERSION IB1_VERSION,
ib.IB1_NAME IB1_NAME, ib.IB1_RELEASE_DATE IB1_RELEASE_DATE, ib.IB1_IBN_REQUIRED IBN_REQUIRED, ib.IB1_filename IB1_filename,
ipp.PROCEDURE_NAME PROCEDURE_NAME, ipp.BLOCK_KD_relevant BLOCK_KD_relevant, ipp.BLOCK_P_relevant BLOCK_P_relevant, ipp.BLOCK_optional BLOCK_optional, ipp.BLOCK_shortname BLOCK_shortname, ipp.BLOCK_standardIBNB BLOCK_standardIBNB, ipp.BLOCK_version BLOCK_version, ipp.BLOCK_NAME BLOCK_NAME, ipp.BLOCK_NAME_EN BLOCK_NAME_EN, ipp.BLOCK_DESC BLOCK_DESC, ipp.BLOCK_TAGS_TAG BLOCK_TAGS_TAG, ipp.BLOCK_AREA BLOCK_AREA, ipp.BLOCK_LOGICAL_LINK BLOCK_LOGICAL_LINK, ipp.BLOCK_LAS BLOCK_LAS, ipp.BLOCK_TIMEOUT BLOCK_TIMEOUT, ipp.BLOCK_POLLINTERVALL BLOCK_POLLINTERVALL,
ipp.BLOCK_CONDITIONS_MANUAL_OPERATIONS BLOCK_CONDITIONS_MANUAL_OPERATIONS, ipp.BLOCK_CONDITIONS_GENERAL_CONDITIONS BLOCK_CONDITIONS_GENERAL_CONDITIONS, ipp.BLOCK_CONDITIONS_BUILDUP_CONDITIONS BLOCK_CONDITIONS_BUILDUP_CONDITIONS, ipp.BLOCK_IBNZ_VSI BLOCK_IBNZ_VSI, ipp.BLOCK_IBNZ_VSI_VERIFIED BLOCK_IBNZ_VSI_VERIFIED,
iss.STEP_NAME STEP_NAME, iss.STEP_DESC STEP_DESC, iss.STEP_DIAG_SERVICE_IDENTIFIER STEP_DIAG_SERVICE_IDENTIFIER, iss.STEP_DIAG_SERVICE_LONG_NAME STEP_DIAG_SERVICE_LONG_NAME, iss.STEP_DIAG_SERVICE_SHORT_NAME STEP_DIAG_SERVICE_SHORT_NAME,
issdp.STEP_DIAG_PARAMETERS_DATA_IDENTIFIER STEP_DIAG_PARAMETERS_DATA_IDENTIFIER, issdp.DIAG_PARAMETER_LONG_NAME DIAG_PARAMETER_LONG_NAME, issdp.DIAG_PARAMETER_SHORT_NAME DIAG_PARAMETER_SHORT_NAME, issdp.DIAG_PARAMETER_MESSAGE_TYPE DIAG_PARAMETER_MESSAGE_TYPE, issdp.DIAG_PARAMETER_RESPONSE_SHORT_NAME DIAG_PARAMETER_RESPONSE_SHORT_NAME, issdp.DIAG_PARAMETER_PATH DIAG_PARAMETER_PATH, issdp.DIAG_PARAMETER_DATATYPE DIAG_PARAMETER_DATATYPE, issdp.DIAG_PARAMETER_VALUE_PROXY_VALUE DIAG_PARAMETER_VALUE_PROXY_VALUE issdp.DIAG_PARAMETER_VALUE_PROXY_TOLERANCE_MAX_VALUE DIAG_PARAMETER_VALUE_PROXY_TOLERANCE_MAX_VALUE issdp.DIAG_PARAMETER_DISPLAYUNIT DIAG_PARAMETER_DISPLAYUNIT
FROM ib1_basic AS ib LEFT JOIN ib1_procedures_procedure AS ipp ON ib.platform_name = ipp.platform_name AND ib.diagnose_adr = ipp.diagnose_adr AND ib.IB1_VERSION = ipp.IB1_VERSION
LEFT JOIN ib1_procedures_procedure_block_steps_step AS iss ON ipp.platform_name = iss.platform_name AND ipp.diagnose_adr = iss.diagnose_adr AND ipp.IB1_VERSION = iss.IB1_VERSION AND ipp.BLOCK_NAME = iss.BLOCK_NAME
INNER JOIN IB1_PROCEDURES_PROCEDURE_BLOCK_STEPS_STEP_DIAG_PARAMETERS AS issdp ON iss.platform_name = issdp.platform_name AND iss.diagnose_adr = issdp.diagnose_adr AND iss.IB1_VERSION = issdp.IB1_VERSION AND iss.BLOCK_NAME = issdp.BLOCK_NAME AND iss.STEP_NAME = issdp.STEP_NAME) as temp
issdp.platform_name = #{platform_name}
and issdp.IB1_VERSION = #{IB1_VERSION}
and issdp.diagnose_adr = #{diagnose_adr}
and issdp.BLOCK_NAME = #{BLOCK_NAME}
and issdp.STEP_NAME = #{STEP_NAME}
