mapper.select:
SELECT diagnose_adr,IB1_NAME,IB1_VERSION,id,IDENTS_IDENT_BGONLINE,IDENTS_IDENT_COMMENT,IDENTS_IDENT_DID,IDENTS_IDENT_NAME,IDENTS_IDENT_S42_ATTRIBUTE,platform_name FROM ib1_bzd_idents


    platform_name = #{platform_name}


    and IB1_VERSION = #{IB1_VERSION}


    and diagnose_adr = #{diagnose_adr}

SELECT DISTINCT IDENTS_DESC FROM IB1_BZD_IDENTS


        platform_name = #{platform_name}


        and IB1_VERSION = #{IB1_VERSION}


        and diagnose_adr = #{diagnose_adr}

SELECT TARGET_DATE FROM IB1_BZD_IDENTS


        platform_name = #{platform_name}


        and IB1_VERSION = #{IB1_VERSION}


        and diagnose_adr = #{diagnose_adr}

SELECT S42_ATTRIBUTES_ADDED FROM IB1_BZD_IDENTS


        platform_name = #{platform_name}


        and IB1_VERSION = #{IB1_VERSION}


        and diagnose_adr = #{diagnose_adr}

mapper.delete:
delete from IB1_BZD_IDENTS


        platform_name = #{platform_name}


        and IB1_VERSION = #{IB1_VERSION}


        and diagnose_adr = #{diagnose_adr}
